package java_util

// java.util.UUID
type UUID struct {
	Value string `hessian:"value"`
}

func (UUID) JavaClassName() string {
	return "java.util.UUID"
}

//String returns a string object representing this UUID.
func (uuid UUID) String() string {
	return uuid.Value
}
